import React from "react";
import "./Skills.css";
import SoftwareSkill from "../../components/softwareSkills/SoftwareSkill";
import { skillsSection } from "../../portfolio";
import { HoverCard } from "react-png-hovercard";
import { Fade } from "react-reveal";

export default function Skills() {
  return (
    <div className="main" id="skills">
      <div className="skills-main-div">
        <Fade right duration={1000}>
          <div className="skills-text-div">
            <h1 className="skills-heading">My Passions</h1>
            <HoverCard
              front={
                <div className="Front">
                  <img
                    src="https://images.unsplash.com/photo-1498910265115-9fb541931cd1?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1089&q=80"
                    alt=""
                    style={{ objectFit: "cover" }}
                  />
                </div>
              }
              back={
                <div className={classes.back}>
                  <p> I would do anything to be there</p>
                </div>
              }
              maxWidth={400}
              animationSpeed={500}
              height={300}
              margin={10}
            />
          </div>
        </Fade>
      </div>
    </div>
  );
}
